select.if:
select m.machineryNO,COUNT(workMachineryID) as '农机数量',SUM(workLength) as '作业里程',SUM(workArea) as '作业面积'
from  agricultural_work w,agricultural_machinery m where w.workMachineryID=m.machineryID

      w.workStartTime >= #{workStartTime}


      w.workEndTime <= #{workEndTime}

  GROUP BY workMachineryID

select province,city,county,COUNT(workMachineryID) as '农机数量',SUM(workLength) as '作业里程',SUM(workArea) as '作业面积'
from  agricultural_work where 1=1


  and   workStartTime >= #{workStartTime}


  and   workEndTime <= #{workEndTime}

GROUP BY county
